/* books 테이블 생성 */

create table books(
	bId varchar(40) not null,
    bName varchar(40),
    bAuthor varchar(40),
    bPublisher varchar(40),
    bClient varchar(40),
    bExplain varchar(600),
    primary key (bId),
    foreign key (bClient) references client (CId)
);

select * from books;

